assert_throws: function "function ()
                {
                    mediaSource.duration = newDuration;
                }" did not throw

assert_throws: function "function ()
                    {
                        mediaSource.duration = newDuration;
                    }" did not throw

assert_throws: remove function "function ()
              {
                  sourceBuffer.remove(-1, 2);
              }" threw object "InvalidAccessError: Failed to execute 'remove' on 'SourceBuffer': The start provided (-1) is outside the range (0, 10)." ("InvalidAccessError") expected object "TypeError" ("TypeError")

assert_throws: remove function "function ()
              {
                  sourceBuffer.remove(11, 12);
              }" threw object "InvalidAccessError: Failed to execute 'remove' on 'SourceBuffer': The start provided (11) is outside the range (0, 10)." ("InvalidAccessError") expected object "TypeError" ("TypeError")

assert_throws: remove function "function ()
              {
                  sourceBuffer.remove(2, 1);
              }" threw object "InvalidAccessError: Failed to execute 'remove' on 'SourceBuffer': The end value provided (1) must be greater than the start value provided (2)." ("InvalidAccessError") expected object "TypeError" ("TypeError")

assert_throws: remove function "function ()
              {
                  sourceBuffer.remove(0, Number.NEGATIVE_INFINITY);
              }" threw object "InvalidAccessError: Failed to execute 'remove' on 'SourceBuffer': The end value provided (-Infinity) must be greater than the start value provided (0)." ("InvalidAccessError") expected object "TypeError" ("TypeError")

assert_throws: remove function "function ()
              {
                  sourceBuffer.remove(0, Number.NaN);
              }" threw object "InvalidAccessError: Failed to execute 'remove' on 'SourceBuffer': The end value provided (NaN) must be greater than the start value provided (0)." ("InvalidAccessError") expected object "TypeError" ("TypeError")

assert_throws: remove function "function ()
              {
                  sourceBuffer.remove(0, 0);
              }" threw object "InvalidAccessError: Failed to execute 'remove' on 'SourceBuffer': The start provided (0) is outside the range (0, 0)." ("InvalidAccessError") expected object "TypeError" ("TypeError")

